// Single-word fruit and vegetable names + common short forms (lowercase)
// Includes plural forms generated by simple English rules and a handful of exceptions.
const BASE = [
  // Fruits
  'apple','apricot','avocado','banana','blackberry','blueberry','boysenberry','cherimoya','cherry','clementine',
  'coconut','cranberry','currant','date','dragonfruit','durian','elderberry','fig','grape','grapefruit','guava',
  'kiwi','kumquat','lemon','lime','lychee','mango','mandarin','nectarine','orange','papaya','passionfruit','peach',
  'pear','persimmon','pineapple','plum','pomegranate','quince','raspberry','starfruit','strawberry','tangerine','ugli',
  'watermelon','melon','cantaloupe',
  'plantain','rambutan','jackfruit','soursop','tamarillo','tamarind','mangosteen',
  'longan','loquat','jujube','mulberry','gooseberry','breadfruit','salak','ackee',
  'ube','honeydew','galia',
  'jambu','mangaba','jabuticaba','lucuma','sapodilla','pulasan','marang','lanzones',
  // Vegetables
  'artichoke','arugula','asparagus','beet','beetroot', 'bean','bokchoy','broccoli','brussels','brusselsprout','cabbage','carrot','soybean', 'edamame',
  'lentil','lentils','chickpea','garbanzo','spinach','lettuce','watercress','microgreen','sprout','sprouts','scallion','springonion','chive','mustardgreen','snowpea','snappea',
  'cassava','taro','chayote','edamame',
  'jicama','sunchoke','lotusroot','burdock','yucca','yuca',
  'cauliflower','celery','chard','chicory','collard','corn','cucumber','daikon','eggplant','endive','fennel','garlic',
  'ginger','kale','leek','lettuce','mushroom','okra','onion','parsnip','pea','pepper','potato','pumpkin','radish','rhubarb',
  'rutabaga','shallot','spinach','squash','sweetpotato','tomato','turnip','yam','zucchini'
  ,'celeriac','kohlrabi','radicchio'
]

// A small map of irregular plurals or preferred plural forms
const IRREGULARS = {
  potato: 'potatoes',
  tomato: 'tomatoes',
  'sweetpotato': 'sweetpotatoes',
  'cantaloupe': 'cantaloupes', // allow plural
  'brussels': 'brussels', // 'brussels' often used as-is (from brussels sprouts); keep safe
  'pea': 'peas'
}

function pluralize(word) {
  if (!word) return word
  if (IRREGULARS[word]) return IRREGULARS[word]
  // words ending with consonant + 'y' -> replace y with ies
  if (/[^aeiou]y$/.test(word)) return word.replace(/y$/, 'ies')
  // words ending with 'o' sometimes take 'es' : handle common ones above, otherwise add 's'
  if (word.endsWith('o')) return word + 's'
  // default: add 's'
  return word + 's'
}

// Build final list including base + plurals
const COMBINED = []
BASE.forEach(w => {
  COMBINED.push(w)
  try {
    const p = pluralize(w)
    if (p && p !== w) COMBINED.push(p)
  } catch (e) {}
})

// normalize: lowercase and unique single-word alphabetic tokens only
const FRUITS_VEGS = Array.from(new Set((COMBINED || []).map(s => (s || '').toString().toLowerCase().trim()).filter(Boolean))).filter(x => /^[a-z]+$/.test(x))
export default FRUITS_VEGS
